using System.Reflection;
namespace Candyland.Blazor;
public partial class CandylandBoardBlazor
{

    [CascadingParameter]
    public CandylandMainViewModel? DataContext { get; set; }
    [Parameter]
    public BasicList<CandylandPlayerItem>? PlayerList { get; set; }
    [Parameter]
    public GameBoardVM? BoardViewModel { get; set; }
    private Assembly GetAssembly => Assembly.GetAssembly(GetType())!;
    private BasicList<CandylandPlayerItem> ModifiedList
    {
        get
        {
            var firstList = PlayerList!.ToBasicList();
            firstList.RemoveAllOnly(x => x.SpaceNumber == 0);
            var nextList = firstList.GroupBy(x => x.SpaceNumber);
            BasicList<CandylandPlayerItem> output = new();
            foreach (var item in nextList)
            {
                output.Add(item.Last());
            }
            return output;
        }
    }
    private static RectangleF GetCastleRectangle => new(374.448f, 6.75f, 100.875f, 67.5f);
    private async Task CastleClicked()
    {
        await BoardViewModel!.CastleAsync();
    }
    private async Task SpaceClicked(int space)
    {
        if (DataContext!.CommandContainer.IsExecuting)
        {
            return;
        }
        if (InProgressHelpers.MoveInProgress)
        {
            return; //you can't do 
        }
        await BoardViewModel!.MakeMoveAsync(space);
    }
